.side-bar-cntr:hover {
  transform: translateZ(0) !important;
}
.side-bar-cntr::after {
  right: calc(100% + 4px);
  transform: translateY(-50%) rotate(45deg);
  width: 8px;
  height: 8px;
  border-radius: 2px;
  box-sizing: border-box;
  border: 4px solid #aaa;
  border-top-color: transparent;
  border-right-color: transparent;
}
body.dark .side-bar-cntr::after {
  border: 4px solid #eee;
  border-top-color: transparent;
  border-right-color: transparent;
}
.side-bar-cntr::before {
  right: calc(100% - 8px);
  transform: translateY(-50%);
  width: 32px;
  height: 48px;
  border-radius: 8px 0 0 8px;
  background-color: #fff;
  border: 1px solid #e9eaec;
}
body.dark .side-bar-cntr::before {
  background-color: #222;
  border-color: transparent;
}
.side-bar-cntr::before, .side-bar-cntr::after {
  content: "";
  transition: 0.24s ease-out;
  cursor: pointer;
  position: fixed;
  top: 50%;
}
.side-bar-cntr:hover::after, .side-bar-cntr:hover::before {
  opacity: 0;
