.feeds-filter {
  background-color: white;
  font-size: 12px;
  width: 100%;
  padding: 12px 16px;
  border-radius: 4px;
  box-sizing: border-box;
  display: none;
  flex-direction: column;
  max-height: 80vh;
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
}
.feeds-filter::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
body.enable-feeds-filter:not(.disable-feeds-filter) .feeds-filter {
  display: flex;
}
.feeds-filter, .feeds-filter * {
  transition: 0.2s ease-out;
  transition-property: border-color, color, background-color;
}
body.dark .feeds-filter {
  color: #eee;
  background-color: #444;
}
.feeds-filter .feeds-filter-header {
  cursor: pointer;
  margin-bottom: 14px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.feeds-filter .feeds-filter-header h1 {
  font-weight: normal;
  font-size: 14px;
  margin: 0;
}
.feeds-filter.collapse .feeds-filter-header {
  margin-bottom: 0;
}
.feeds-filter.collapse .feeds-filter-header .be-icon {
  transform: rotate(180deg);
}
.feeds-filter.collapse > :not(.feeds-filter-header) {
  display: none;
}
.feeds-filter h2 {
  font-weight: bold;
  font-size: 13px;
  margin: 0;
  margin-bottom: 8px;
}
.feeds-filter .filter-type-switch {
  flex: 0 0 49%;
}
.feeds-filter .filter-side-card-switch {
  flex: 0 0 100%;
}
.feeds-filter .filter-types {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  margin-bottom: 18px;
}
body.by-type .feeds-filter h2:nth-of-type(1),
body.by-type .feeds-filter .filter-types {
  display: none;
}
.feeds-filter .filter-patterns:not(:empty) {
  margin-bottom: 4px;
}
.feeds-filter .filter-patterns .pattern {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 4px 6px;
  border-radius: 4px;
  font-size: 12px;
  border: 1px solid rgba(136, 136, 136, 0.2666666667);
}
.feeds-filter .filter-patterns .pattern:not(:last-child) {
  margin-bottom: 4px;
}
.feeds-filter .filter-patterns .pattern .be-icon {
  cursor: pointer;
}
.feeds-filter .add-pattern {
  display: flex;
  align-items: center;
  margin-bottom: 18px;
}
.feeds-filter .add-pattern input {
  font-size: 12px;
}
.feeds-filter .add-pattern .be-button {
  margin-left: 6px;
  padding: 4px 6px;
