  z-index: 0;
}

.z-top-container,
.international-header {
  position: sticky !important;
  top: 0 !important;
  z-index: 1;
}

.van-popper-avatar,
.van-popper-vip,
.van-popper-favorite,
.van-popper-history,
.van-popper-upload,
.popover-channe,
.popover-game,
.popover-live,
.custom-navbar {
  position: fixed !important;
}

.sticky-bar {
  display: none !important;
}

.left-panel,
.right-panel {
  position: fixed !important;
}

.home-container {
  --left-width: 244px;
  --center-width: 632px;
}
@media screen and (min-width: 1921px) {
  .home-container {
    --left-width: 12.71vw;
    --center-width: 32.92vw;
  }
}
@media screen and (min-width: 2497px) {
  .home-container {
    --left-width: 317px;
    --center-width: 822px;
  }
}

.center-panel {
  margin-left: calc(var(--left-width) + 8px) !important;
}

.right-panel {
  margin-left: calc(var(--left-width) + 16px + var(--center-width)) !important;
}

.live-panel .live-up-list {
  overflow: auto;
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
}
.live-panel .live-up-list::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}

.live-up-list {
  max-height: calc(100vh - 315px);
}
body.enable-feeds-filter .live-up-list {
  max-height: calc(100vh - 370px);
}
body.feeds-filter-side-block-profile .live-up-list {
  max-height: calc(100vh - 180px);
}

.bili-dyn-live-users {
  display: flex !important;
  flex-direction: column !important;
}

.bili-dyn-live-users__body {
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
  margin: 0px -16px -16px -16px;
  padding: 0px 16px 4px 16px;
  max-height: calc(100vh - 318px);
}
.bili-dyn-live-users__body::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
body.enable-feeds-filter .bili-dyn-live-users__body {
  max-height: calc(100vh - 374px);
}
body.feeds-filter-side-block-profile .bili-dyn-live-users__body {
  max-height: calc(100vh - 178px);
}

.adaptive-scroll {
  min-height: unset !important;
}
.adaptive-scroll .scroll-content {
  position: static !important;
}

.bili-dyn-home--member {
  --center-width: 632px;
  --gap: 8px;
  --fixed-offset: calc(var(--center-width) + (100% - var(--center-width)) / 2 + var(--gap));
}
.bili-dyn-home--member section.sticky {
  position: static !important;
}
.bili-dyn-home--member aside {
  position: fixed !important;
}
.bili-dyn-home--member aside.left {
  right: var(--fixed-offset);
}
.bili-dyn-home--member aside.right {
  left: var(--fixed-offset);
}
@media screen and (min-width: 1921px) {
  .bili-dyn-home--member {
    --center-width: 32.92vw;
  }
}
@media screen and (min-width: 2497px) {
  .bili-dyn-home--member {
    --center-width: 822px;
  }
