  display: flex;
  align-items: center;
  flex-direction: column;
  gap: 0;
  flex: 1 0 auto;
  align-self: flex-end;
}
.gesture-preview-content .progress .videoshot {
  height: 70px;
  width: 120px;
  margin-bottom: 8px;
  border-radius: 4px;
}
.gesture-preview-content .progress .name {
  color: #aaa;
  padding: 4px 6px;
  margin-bottom: 6px;
}
.gesture-preview-content .progress .progress-label {
  margin-bottom: 12px;
}
.gesture-preview-content .progress .preview {
  display: flex;
  align-items: center;
  gap: 0;
  margin-bottom: 6px;
}
.gesture-preview-content .progress .preview .diff {
  color: var(--theme-color);
  margin-right: 6px;
}
.gesture-preview-content .progress .preview .seek-mode {
  padding: 4px 6px;
  border-radius: 4px;
  background-color: rgba(136, 136, 136, 0.2666666667);
